           function add() {
    seconds++;
    localStorage.setItem("seconds", seconds);
    document.getElementById('secondsInput').value = seconds;
    if (seconds >= 60) {
        seconds = 0;
        minutes++;
        localStorage.setItem("minutes", minutes);
        document.getElementById('minutesInput').value = minutes;
        if (minutes >= 60) {
            minutes = 0;
            hours++;
            localStorage.setItem("hours", hours);
            document.getElementById('hoursInput').value = hours;
        }

    }
    if(minutes >= 1 && minutes >= 30)
    {
      alert('Times Up, Submitting your results ...');
      document.getElementById("myForm").submit();
    }
    calculations = (hours * 60 * 60) + (minutes * 60) + seconds;
  var xhttp = new XMLHttpRequest();
  xhttp.onreadystatechange = function() {
    if (this.readyState == 4 && this.status == 200) {
     //document.getElementById("demo").innerHTML = this.responseText;
    }
  };
  xhttp.open("GET", "{{ url('/') }}/UpdateTimer/"+hours+'/'+minutes+'/'+seconds, true);
  xhttp.send();

    h1.textContent = (hours ? (hours > 9 ? hours : "0" + hours) : "00") + ":" + (minutes ? (minutes > 9 ? minutes : "0" + minutes) : "00") + ":" + (seconds > 9 ? seconds : "0" + seconds);

    timer();
}

 function timer() 
